import React, { useState, useEffect } from 'react';
import './App.css';
import Header from './components/Header';
import Tasks from './components/Tasks';
import AddTask from './components/AddTask';
import { v4 as uuidv4 } from 'uuid';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import TaskDetails from './components/TaskDetails';
import axios from 'axios';
import Modal from 'react-modal';

Modal.setAppElement('#root');

const App = () => {
  const [tasks, setTasks] = useState([]);
  const [modalIsOpen, setModalIsOpen] = useState(true); // Definindo o estado inicial como true para a modal iniciar aberta

  useEffect(() => {
    const fetchTasks = async () => {
      const { data } = await axios.get(
        'https://jsonplaceholder.cypress.io/todos?_limit=10'
      );
      setTasks(data);
    };
    fetchTasks();
  }, []);

  const handleTaskClick = (taskId) => {
    const newTasks = tasks.map((task) => {
      if (task.id === taskId) {
        return { ...task, completed: !task.completed };
      }
      return task;
    });

    setTasks(newTasks);
  };

  const handleTaskAddition = (taskTitle) => {
    const newTasks = [
      ...tasks,
      {
        title: taskTitle,
        id: uuidv4(),
        completed: false,
      },
    ];
    setTasks(newTasks);
    closeModal();
  };

  const handleTaskDeletion = (taskId) => {
    const newTasks = tasks.filter((task) => task.id !== taskId);
    setTasks(newTasks);
  };

  const openModal = () => {
    setModalIsOpen(true);
  };

  const closeModal = () => {
    setModalIsOpen(false);
  };

  return (
    <Router>
       <Modal
                  isOpen={modalIsOpen}
                  onRequestClose={closeModal}
                  contentLabel="Add Task Modal"
                  className="modal"
                  style={{
                    overlay: {
                      backgroundColor: 'rgba(0, 0 ,0, 0.8)'
                    },
                    content: {
                      width: '30%',
                      border: '1px solid green',
                      background: '#839cff',
                      borderRadius: '20px',
                      padding: '20px'
                    }
                  }}
                >
      <div className="container">

        <Header />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <Home
                  tasks={tasks}
                  handleTaskClick={handleTaskClick}
                  handleTaskDeletion={handleTaskDeletion}
                  openModal={openModal}
                />

                  <h2>Add Task</h2>
                  <AddTask handleTaskAddition={handleTaskAddition} />
                  <button onClick={closeModal}>Close</button>

              </>
            }
          />
          <Route path="/:taskTitle" element={<TaskDetails />} />
        </Routes>

      </div>
      </Modal>
    </Router>
  );
};

function Home({ tasks, handleTaskClick, handleTaskDeletion, openModal }) {
  return (
    <>
      <button className="btn-add-task" onClick={openModal}>
        Add Task
      </button>
      <Tasks
        tasks={tasks}
        handleTaskClick={handleTaskClick}
        handleTaskDeletion={handleTaskDeletion}
      />
    </>
  );
}

export default App;
